import ExpenseItem from "./ExpenseItem";
import { useState } from "react";
import Card from "../UI/card";
import "./Expenses.css";
import ExpensesFilter from "./ExpensesFilter";
function Expenses(props) {
  let dummyarray = props.items;
  const [filteredYear, setFilteredYear] = useState("2020");

  const SaveFilterDataHandler = (filterData) => {
    setFilteredYear(filterData);
    dummyarray = props.items.filter(
      (expense) => props.items.date[-4] === filterData
    );
  };
  return (
    <div>
      <Card className="expenses">
        <ExpensesFilter
          selected={filteredYear}
          onSaveFilterData={SaveFilterDataHandler}
        />
        {dummyarray.map((expense) => (
          <ExpenseItem
            key={expense.id}
            title={expense.title}
            amount={expense.amount}
            date={expense.date}
          />
        ))}
      </Card>
    </div>
  );
}

export default Expenses;
